PRC.LOB_FIELD:
      create or replace procedure COLVIR.Z_116_GETCONTACTBYCARD (pCode  in varchar2, pCardcode varchar2,
                               pOutput out Clob) is

  vID      number;
  vDep_id  number;
  vMobid   varchar2(50) := 7595;
begin

  Z_116_PKGCONNECT.pOpen();


  select id,dep_id
    into vID, vDep_id
    from NV_CRD_LIST
   where cli_code = pCode
     and cardcode = pCardcode;

 pOutput := T_PkgDeaPrm.fPar(vID, vDep_id, vMobid);

  exception
   when others then
    pOutput := sqlerrm;

end Z_116_GETCONTACTBYCARD;
